import React, { useRef, useState } from 'react';
import VideoFooter from "./VideoFooter";

import './Video.css';

function Video() {
    const [playing, setPlay] = useState(false);
    const videRef = useRef(null);

    const onVideoPress = () => {
        if (playing) {
            videRef.current.pause();
            setPlay(false);
        } else {
            videRef.current.play();
            setPlay(true);
        }
    };

    return (
        <div className="video">
            <video
                className="video__player"
                src="https://endtest-videos.s3-us-west-2.amazonaws.com/documentation/endtest_data_driven_testing_csv.mp4"
                ref={videRef}
                onClick={onVideoPress}
                loop
            ></video>
            <VideoFooter channel="kirin" description="check this out!!" song="User - Yeah" />
        </div>
    );
}

export default Video
