import React from 'react';
import './videoFooter.css';
import MusicNoteIcon from '@material-ui/icons/MusicNote';
import Ticker from 'react-ticker';


function videoFotter({ channel, description, song }) {
    return (
        <div className="videoFotter">
            <div className="videoFotter__text">
                <h3>@{channel}</h3>
                <p>{description}</p>
                <div className="videoFotter__ticker">
                    <MusicNoteIcon />
                      <Ticker mode="smooth" >
                        {({ index }) => (
                          <>
                          <p>{song}</p>
                          </>
                        )}
                    </Ticker>
                </div>

            </div>
             <img
                className="videoFooter__record"
                src="https://static.thenounproject.com/png/934821-200.png"
                alt=""
            />
            <img className="videoFotter_record" src="https://static.thenounproject.com/png/934821-200.png" alt=""/>
        </div>
    )
}

export default videoFotter
