import React from 'react';
import './Video.css';

function Video() {
    const videRef = useRef(null);

    const onVideoPress = () => {
        videRef.current.play();
    };

    return (
        <div className="video">
            <video
                className="video__player"
                src=""
                ref={videRef}
                onClick={onVideoPress}
                loop
            ></video>
        </div>
    );
}

export default Video
